mapper.select:
SELECT s.*
FROM voc_survey s
WHERE 1 = 1

    AND (
    s.title LIKE CONCAT('%', #{vo.keyword}, '%')
        OR s.description LIKE CONCAT('%', #{vo.keyword}, '%')
    )


    AND s.is_published = #{vo.isPublished}


    AND s.is_deleted = #{vo.isDeleted}


    AND s.is_star = #{vo.isStar}


    AND s.create_by = #{vo.createBy}
